    function addToCart(i,id){
        let item = document.getElementById(id).getElementsByClassName("item")[i]
        let entry = {
            name : item.getElementsByClassName('name')[0].children[0],
            img : item.getElementsByClassName('img')[0].children[0],
            breed: item.getElementsByClassName("breed")[0].children[0]
        }
        item.getElementsByClassName("icon")[1].children[0].src ="data:image/png;base64, "
        myCart.push(entry)
        console.log(myCart);
    }

    function newDiv(className){
        let item = document.createElement('div');
        item.className = className;
        return item;
    }
    function newElement(element,className){
        let item = document.createElement(element);
        item.className = className;
        return item;
    }

    function createTextChild(element, value){
        let item = document.createElement(element);
        item.innerHTML = value;
        return item;
    }
    function createImgChild(value){
        let item = document.createElement("img");
        item.src = value;
        return item;
    }

    function createLinkChild(href){
        let item = document.createElement("a");
        item.href = href;
        return item;
    }

    function create(obj, target){
        // create all divs needed
        let item = newDiv("item");
        let header = document.createElement('header');
        let name = newDiv("name");
        let breed = newDiv('breed');
        let img = newDiv("img")
        let info = newDiv("info")
        let actions = newDiv("actions");
        let icons = [newDiv("icon"),newDiv("icon"),newDiv("icon")];
        
        //add structure
        name.appendChild(createTextChild("h3",obj.name));
        breed.appendChild(createTextChild("p",obj.breed));
        header.appendChild(name);
        header.appendChild(breed);
        item.appendChild(header);

        img.appendChild(createImgChild(obj.img));
        item.appendChild(img);

        info.appendChild(createTextChild("p", "Age:"+ obj.age));
        info.appendChild(createTextChild("p", "Size:"+ obj.size));
        item.appendChild(info);

       
        icons[0].appendChild( createImgChild(" https://cdn.iconscout.com/icon/free/png-512/free-heart-1121-433832.png?f=avif&w=256"));
        icons[0].setAttribute("onclick", "heart("+obj.id+",'"+obj.species+"')");

        icons[1].appendChild(createImgChild(" ./assets/cart-shopping-solid.svg"));
        icons[1].setAttribute("onclick", "addToCart("+obj.id+",'"+obj.species+"')");

        let link = createLinkChild(obj.link);
        link.appendChild(createImgChild("https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fis2-ssl.mzstatic.com%2Fimage%2Fthumb%2FPurple%2Fv4%2F63%2F2b%2F48%2F632b4878-c7da-9440-e1f1-e9405fd7f318%2Fsource%2F512x512bb.jpg&f=1&nofb=1&ipt=1dc167c220b43b1e461b9987857f95f0e7b3ce9f7f9d92d922636ba1999434b2&ipo=images"));

        icons[2].appendChild(link);
        for(let i= 0; i<icons.length;i++){
            actions.appendChild(icons[i]);
        }

        item.appendChild(actions);
        target.appendChild(item);
        
    }
    newItem();
    function newItem(){
        for(let i =0; i<dogs.length;i++){
        create(dogs[i],document.getElementsByClassName('item-container')[0]);
        }
        for(let j =0; j<cats.length;j++){
            create(cats[j],document.getElementsByClassName('item-container')[1]);
            }
    }